//Abre uma janela para adcionar cliente
jQuery("#Btn_conv_1").click(function(){

	// mensagen de carregamento
	jQuery("#msg_loading").html(" Aguarde... ");
	//abre a tela de preload
	modal.show();

	LoadContent('assets/convenio/Frm_convenio.php','content');
});

// faz o update dos dados
jQuery(function() {

	jQuery("#Btn_conv_2").click(function(event) {

		 // mensagen de carregamento
		 jQuery("#msg_loading").html(" Aguarde... ");

		 //abre a tela de preload
		 modal.show();

		 //desabilita o envento padrao do formulario

		 event.preventDefault();

		 jQuery.ajax({
		 	async: true,
		 	url: "assets/convenio/Controller_convenios.php",
		 	type: "post",
		 	data:jQuery("#FrmConvenio").serialize(),
		 	success: function(resultado){
		 		if(jQuery.isNumeric(resultado)){
					// mensagen de carregamento
					jQuery("#msg_loading").html(" Carregando ");
					// variavel com id
					var conv_id=resultado;
					// recarrega a pagina
					LoadContent('assets/convenio/Frm_convenio.php?conv_id='+conv_id.replace(/[^\d]+/g,'')+'','content');
				}else{
						//abre a tela de preload
						modal.hide();
						UIkit.notify(""+resultado+"", {status:'danger',timeout: 2500})
					}
				},
				error:function (){
					UIkit.modal.alert("Erro ao enviar dados! Erro 404");/*erro de caminho invalido do arquivo*/
					modal.hide();
				}

			});

 	});
});

