// grava os dados no banco de dados

jQuery(function() {

 jQuery("#Btn_vendor_2").click(function(event) {

 // mensagen de carregamento
 jQuery("#msg_loading").html(" Aguarde ... ");

 //abre a tela de preload
 modal.show();

 //desabilita o envento padrao do formulario
 event.preventDefault();

 jQuery.ajax({
				async: true,
				url: "assets/vendedor/Controller_vendedores.php",
				type: "post",
				data:jQuery("#Frmvendedor").serialize(),
				success: function(resultado) {
												if(jQuery.isNumeric(resultado)){
														// mensagen de carregamento
														jQuery("#msg_loading").html(" Carregando ");
														// variavel com id
														var id=resultado;
														// recarrega a pagina
														LoadContent('assets/vendedor/Frm_vendedor.php?vendor_id='+id.replace(/[^\d]+/g,'')+'','content');
												}else{
														//abre a tela de preload
														modal.hide();
														UIkit.notify(""+resultado+"", {status:'danger',timeout: 2500})
													}
				},
				error:function (){
					UIkit.modal.alert("Erro ao enviar dados! Erro 404");/*erro de caminho invalido do arquivo*/
					modal.hide();
					}

			});

		});

});